window.KitoLoader={loadScript:function(e){return new Promise((n,t)=>{const o=window.document.createElement("script");o.type="text/javascript",o.onload=n,o.onerror=t,o.src=e,window.document.head.append(o)})},loadStyle:function(e){return new Promise((n,t)=>{const o=window.document.createElement("link");o.rel="stylesheet",o.onload=n,o.onerror=t,o.href=e,window.document.head.append(o)})},loadHtml:function(e){return fetch(e).then(e=>e.text()).then(e=>{const n=window.document.createElement("span");n.innerHTML=e,window.document.body.append(n)})}};